// React controlled forms
// Each input needs a value and onChange. The onChange needs a handler and the value needs to match a state object

import React, { Component } from "react";

export class UncontrolledForm extends Component {
  constructor(props) {
    super(props);
    this.inputName = React.createRef();
    this.inputCategory = React.crea
  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.inputName.current.value);
  };

  render() {
    return (
      <div>
        <h2>Please fill out the Uncontrolled form below</h2>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor="id-name">Your Name: </label>
            <input id="id-name" name="name" type="text" ref={this.inputName} />
          </div>

          <div>
            <label htmlFor="id-category">Query category: </label>
            <select name="category" id="id-category">
              <option value="website">Website issue</option>
              <option value="order">Order issue</option>
              <option value="general">General inquiry</option>
            </select>
          </div>
          <div>
            <label htmlFor="id-comments">Comments:</label>
            <textarea id="id-comments" name="comments" />
          </div>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}

export default UncontrolledForm;
